#include <iostream>#include <fstream>#include "file.h"#include "tree.h"//#include "tree.cpp"using namespace std;void help(){	cout << "Available operations:" << endl;	cout << "add	show	delete	count	help	exit" << endl;}int main(){	Node *tree = NULL;	File * t=NULL;	string str;	cout << "Welcome to my program!" << endl;	help();	while (!cin.eof()){		cout << endl << ">";		cin >> str;		if (str == "exit")			break;		if (str == "add"){			cout << "Pleasy specify your file type ('t' for txt, 'v' for video, 'i' for image)" << endl;			cin >> str;						if (str=="i"){				cout << "Please enter a file name" << endl;				cin >> str;				t = new Image_File (str);			}else			if (str=="v"){				cout << "Please enter a file name" << endl;				cin >> str;				t = new Video_File (str);			}else			if (str=="t"){				cout << "Please enter a file name" << endl;				cin >> str;				t = new Txt_File (str);			}else{				cout << "Unknown type, please try again"<< endl;				continue;			}			if (tree == NULL)				tree = new Node(t);			else				tree->AddToSubTree(t);			cout << "You have successfully added your file" << endl;			continue;		}		if (str == "delete"){			if (tree == NULL){				cout << "List is empty" << endl;				continue;			}			tree->DeleteSubTree();			delete tree;			cout << "List is empty now" << endl;			tree = NULL;			continue;		}		if (str == "show"){			if (tree == NULL){				cout << "List is empty" << endl;				continue;			}else{				tree->PrintSubTree();				continue;			}		}		if (str == "count"){			cout << "Number of files: " << File::get_quantity()<< endl;			continue;		}		if (str == "help"){			help();			continue;		}		cout << "Unknown command, please try again" << endl;	}	if (tree != NULL)		tree->DeleteSubTree();	delete tree;	return 0;}